    <script>
        
        function actualizarInfo() {
            consultaUbicaciones();
            setTimeout(function(){ actualizarInfo(); }, 5000);
        }
                
        function consultaUbicaciones() {
            jQuery.ajax({
                url: "<?php echo base_url(); ?>/consultas_ajax/consultaUbicaciones.php",
                cache: false,
                contentType: "text/html; charset=UTF-8",
                success: function(response){
                    var ubicacionesArray = response.split("@@");
                    for (i=0; i<ubicacionesArray.length; i++) {
                        var ubicacionTemp = ubicacionesArray[i].split("|");
                        $('[id]').each(function () {
                            if (ubicacionTemp[0].replace("\"", "") == this.id) {
                                if (ubicacionTemp[1] == "0") {
                                    document.getElementById(this.id).style.backgroundColor = "red";
                                }
                                if (ubicacionTemp[1] == "1") {
                                    document.getElementById(this.id).style.backgroundColor = "green";
                                }
                                if (ubicacionTemp[1] == "2") {
                                    document.getElementById(this.id).style.backgroundColor = "orange";
                                }
                            }
                        });
                    }
                },
                error: function(response){
                    alert("Error");
                }
            });        
        }
    </script>
    <script>setTimeout(function(){ actualizarInfo();}, 5000);</script>
